#!/bin/bash
 
CRTB64=''

# cp -f /usr/lib/jvm/zulu8-ca-amd64/jre/lib/security/cacerts /opt/liferay/data/
cp -f /usr/lib/jvm/zulu11/lib/security/cacerts /opt/liferay/data/
echo $CRTB64 | base64 -d > tmpcrt.pem
keytool -import -alias Honda -keystore /opt/liferay/data/cacerts -file tmpcrt.pem -storepass changeit -noprompt
rm tmpcrt.pem